require(['jquery'], function($) {
  //  var objectNumber = $('input[name="defacto-tags-object-number"').val();
  var objectNumber = 0;
  $('select[name="tags"]').on('change', function(event) {
    // Remove the tag inputs previously set
    $('#mainContentArea > form .defacto-tag-input').remove();
    var form = $('#mainContentArea > form');
    var tags = $(this).val();
    if (tags !== null) {
      tags.each(function(item, index) {
        var node = $('<input type="hidden" class="defacto-tag-input" name="XWiki.TagClass_' + objectNumber + '_tags"/>;');
        node.attr('value', item);
        form.append(node);
      });
    } else {
      var node = $('<input type="hidden" class="defacto-tag-input" name="XWiki.TagClass_' + objectNumber + '_tags"/>;');
      node.attr('value', '');
      form.append(node);
    }
    /*form.find('.xwiki-pinned-child-pages li').each(function (index) {
        var reference = $(this).data('reference')
        var node = $('&lt;input type="hidden" class="xwiki-pinned-child-pages-input" name="XWiki.PinnedChildPagesClass_' + objectNumber + '_pinnedChildPages"/&gt;');
        node.attr('value', reference);
        form.append(node);
    });*/
  });
});

