xwikidoc.content:
{{include reference="RssUtils"/}}

{{groovy}}
def hql = "select doc.fullName, doc.title from XWikiDocument as doc where doc.fullName not like :location and doc.hidden = false order by doc.date desc";
def query = services.query.hql(hql);
query.setLimit(20)
query.bindValue('location', 'Media%')
def results = query.execute();

def utils = new RssUtils();
def feed = utils.createFeed(results, services, xwiki);
utils.printFeed(feed, out, xcontext, response)
{{/groovy}}
